CREATE DEFINER=`root`@`localhost` PROCEDURE `uspSelMessageCode`(IN `message_code_id` INT)
    READS SQL DATA
BEGIN
	SELECT
    	M.id,
        CONCAT(M.message,';;',M.message_type) AS 'message'
    FROM
    	`message_codes` M
    WHERE
    	CASE WHEN message_code_id = 0
        THEN 0
        ELSE M.id
        END = message_code_id;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `uspSelParameter`(IN `parameter_id` INT)
    READS SQL DATA
BEGIN
	SELECT
    	P.id,
        P.parameter_name,
        P.parameter_type,
        P.parameter_value
    FROM
    	`parameters` P
    WHERE
    	CASE WHEN parameter_id = 0
        THEN 0
        ELSE P.id
        END = parameter_id;
END$$

CREATE DEFINER=`root`@`localhost` PROCEDURE `uspSelUserLogin`(IN `user_name` VARCHAR(150), IN `user_password` VARCHAR(150))
    READS SQL DATA
BEGIN
	SELECT
    	U.id,
        U.user_name,
        U.full_name,
        U.email,
        U.phone,
        U.is_enabled
    FROM
    	`users` U
	WHERE
    	U.user_name = user_name
        AND U.user_password = user_password
    ;
END$$

